mapper.insert:

	select 
		seq_board.nextval boardid 
	from dual

INSERT INTO board (
	  pid
	, boardid
	, title
	, contents
	, writer_uid
	, reg_date
) VALUES (
	  0
	, #{boardid}
	, #{title}
	, #{contents}
	, #{writer_uid}
	, sysdate
)


	select 
		seq_board.nextval boardid 
	from dual

INSERT INTO board (
	  pid
	, boardid
	, title
	, contents
	, writer_uid
	, reg_date
) VALUES (
      #{pid} 
	, #{boardid}
	, #{title}
	, #{contents}
	, #{writer_uid}
	, sysdate
)

mapper.update:
update board set 
	  title=#{title}
	, contents=#{contents} 
where boardid=#{boardid}

update board set 
	view_count = view_count + 1 
where boardId = #{boardId}

mapper.delete:
delete from board 
where boardid in

	#{boardid}

mapper.select:
select * from (
    select * from board

    where boardid < #{boardid} 

    order by boardid desc
) A

where rownum <= N
