mapper.insert:

	select 
		seq_comments.nextval commentid 
	from dual

INSERT INTO comments (
    comment_boardid
	, commentid
	, contents
	, writer_uid
) VALUES (
    #{comment_boardid}
	, #{commentid}
	, #{contents}
	, #{writer_uid}
)

mapper.update:
update from comments set
				delete_yn='Y' 
where commentid=#{commentid}
